import React, { useState } from "react";
import { NavLink as RouterNavLink } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

import { useLocation } from "react-router-dom";

import {
  Collapse,
  Container,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  Button,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
} from "reactstrap";

import { useAuth0 } from "@auth0/auth0-react";
import { faUnderline } from "@fortawesome/free-solid-svg-icons";

const NavBar = () => {
  function useQuery() {
    console.log("in the hook ", useLocation().search);
    return new URLSearchParams(useLocation().search);
  }
  let query = useQuery();
  const Culture = () => {
    const parsedHash = new URLSearchParams(window.location.hash.substr(1));
    let culture = query.get("culture") ?? parsedHash.get("culture");
    return culture;
  };
  const Back = () => {
    const parsedHash = new URLSearchParams(window.location.hash.substr(1));
    let back = query.get("enableBack") ?? parsedHash.get("enableBack");
    return back === "true";
  };
  const Skip = () => {
    const parsedHash = new URLSearchParams(window.location.hash.substr(1));
    let skip = query.get("enableSkip") ?? parsedHash.get("enableSkip");
    return skip === "true";
  };
  const setHeader = () => {
    const parsedHash = new URLSearchParams(window.location.hash.substr(1));
    let header = query.get("hideHeader") ?? parsedHash.get("hideHeader");
    return header === "true";
  };
  const setFooter = () => {
    const parsedHash = new URLSearchParams(window.location.hash.substr(1));
    let footer = query.get("hideFooter") ?? parsedHash.get("hideFooter");
    return footer === "true";
  };
  const [enableSkip, setEnableSkip] = useState(Skip() || undefined);
  const [enableBack, setEnableBack] = useState(Back() || undefined);
  const [hideHeader, setHideHeader] = useState(setHeader() || undefined);
  const [hideFooter, setHideFooter] = useState(setFooter() || undefined);
  const [culture, setCulture] = useState(Culture() || "en-us");

  console.log(
    "-------->",
    setCulture,
    setEnableSkip,
    setEnableBack,
    setHideHeader,
    setHideFooter
  );
  const AffId = () => {
    const parsedHash = new URLSearchParams(window.location.hash.substr(1));
    let culture = query.get("affid") ?? parsedHash.get("affid");
    return culture;
  };
  const getAAI = () => {
    const parsedHash = new URLSearchParams(window.location.hash.substr(1));
    let culture = query.get("aai") ?? parsedHash.get("aai");
    return culture;
  };
  const [aai, setAAI] = useState(getAAI() || {});
  const [affid, setAffId] = useState(AffId() || "0");
  console.log(setAffId, setAAI);
  const [isOpen, setIsOpen] = useState(false);
  const { user, isAuthenticated, loginWithRedirect, logout } = useAuth0();
  const toggle = () => setIsOpen(!isOpen);

  const logoutWithRedirect = () =>
    logout({
      returnTo: window.location.origin,
    });

  return (
    <div className="nav-container">
      <Navbar color="light" light expand="md">
        <Container>
          <NavbarBrand className="logo" />
          <NavbarToggler onClick={toggle} />
          <Collapse isOpen={isOpen} navbar>
            <Nav className="mr-auto" navbar>
              <NavItem>
                <NavLink
                  tag={RouterNavLink}
                  to="/"
                  exact
                  activeClassName="router-link-exact-active"
                >
                  Home
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink
                  tag={RouterNavLink}
                  to="/otp"
                  exact
                  activeClassName="router-link-exact-active"
                >
                  OTP
                </NavLink>
              </NavItem>
              {isAuthenticated && (
                <NavItem>
                  <NavLink
                    tag={RouterNavLink}
                    to="/external-api"
                    exact
                    activeClassName="router-link-exact-active"
                  >
                    External API
                  </NavLink>
                </NavItem>
              )}
            </Nav>
            <Nav className="d-none d-md-block" navbar>
              {!isAuthenticated && (
                <NavItem>
                  <Button
                    id="qsLoginBtn"
                    color="primary"
                    className="btn-margin"
                    onClick={() =>
                      loginWithRedirect({
                        culture: culture,
                        affid: affid,
                        aai: JSON.stringify(aai),
                        enableBack: enableBack,
                        enableSkip: enableSkip,
                        hideHeader: hideHeader,
                        // affid: AffId(),
                        // fragment: `culture=pl-pl&aff_id=105`,
                        // &aai=${JSON.stringify(
                        //   {
                        //     ea: "value",
                        //     cc: "value",
                        //   }
                        // )}`,
                        // appState: {
                        //   returnTo: "?culture=en-gb&aff_id=105",
                        // },
                      })
                    }
                  >
                    Log in
                  </Button>
                </NavItem>
              )}
              {isAuthenticated && (
                <UncontrolledDropdown nav inNavbar>
                  <DropdownToggle nav caret id="profileDropDown">
                    <img
                      src={user.picture}
                      alt="Profile"
                      className="nav-user-profile rounded-circle"
                      width="50"
                    />
                  </DropdownToggle>
                  <DropdownMenu>
                    <DropdownItem header>{user.name}</DropdownItem>
                    <DropdownItem
                      tag={RouterNavLink}
                      to="/profile"
                      className="dropdown-profile"
                      activeClassName="router-link-exact-active"
                    >
                      <FontAwesomeIcon icon="user" className="mr-3" /> Profile
                    </DropdownItem>
                    <DropdownItem
                      id="qsLogoutBtn"
                      onClick={() => logoutWithRedirect()}
                    >
                      <FontAwesomeIcon icon="power-off" className="mr-3" /> Log
                      out
                    </DropdownItem>
                  </DropdownMenu>
                </UncontrolledDropdown>
              )}
            </Nav>
            {!isAuthenticated && (
              <Nav className="d-md-none" navbar>
                <NavItem>
                  <Button
                    id="qsLoginBtn"
                    color="primary"
                    block
                    onClick={() =>
                      loginWithRedirect({
                        culture: Culture(),
                        affid: AffId(),
                        enableBack: enableBack(),
                        enableSkip: enableSkip(),
                        hideHeader: setHeader(),
                      })
                    }
                  >
                    Log in
                  </Button>
                </NavItem>
              </Nav>
            )}
            {isAuthenticated && (
              <Nav
                className="d-md-none justify-content-between"
                navbar
                style={{ minHeight: 170 }}
              >
                <NavItem>
                  <span className="user-info">
                    <img
                      src={user.picture}
                      alt="Profile"
                      className="nav-user-profile d-inline-block rounded-circle mr-3"
                      width="50"
                    />
                    <h6 className="d-inline-block">{user.name}</h6>
                  </span>
                </NavItem>
                <NavItem>
                  <FontAwesomeIcon icon="user" className="mr-3" />
                  <RouterNavLink
                    to="/profile"
                    activeClassName="router-link-exact-active"
                  >
                    Profile
                  </RouterNavLink>
                </NavItem>
                <NavItem>
                  <FontAwesomeIcon icon="power-off" className="mr-3" />
                  <RouterNavLink
                    to="#"
                    id="qsLogoutBtn"
                    onClick={() => logoutWithRedirect()}
                  >
                    Log out
                  </RouterNavLink>
                </NavItem>
              </Nav>
            )}
          </Collapse>
        </Container>
      </Navbar>
    </div>
  );
};

export default NavBar;
